mapper.update:
UPDATE sessions
SET message_count = COALESCE(message_count,0) + 1,
    last_message_at = #{ts},
    updated_at = #{ts}
WHERE id = #{id}

mapper.select:
SELECT id, anon_id, title, channel, message_count, last_message_at, created_at, updated_at, user_id
FROM sessions
WHERE id = #{id}

SELECT id, anon_id, title, channel, message_count, last_message_at, created_at, updated_at, user_id
FROM sessions
WHERE user_id = #{userId}
ORDER BY last_message_at DESC, created_at DESC
LIMIT #{limit}
